import React, { useState } from 'react';
let board = [];
for(let i = 0; i< 30; i++){
    board.push([]);
  for(let j = 0; j < 50; j++){
    board[i].push(Math.random() < .5 ? 1 : 0);
  }
}

export const Board = () => {
  const [boardf, setBoard] = useState(board);
  const handleClick = (e) => {
      let newBoard = [...boardf];
    const x = e.target.id.split(',')[0];
    const y = e.target.id.split(',')[1];
    newBoard[x][y]= newBoard[x][y] === 0 ? 1:0;
    setBoard(newBoard);
  }
  return (<div className="wrapper">
    <div className="board">
    {
    board
      .map(
        (i, index1) => i.map(
            (j, index2) => <div
                id={[index1, index2]}
                onClick={handleClick}
                key={index1+""+index2}
                className="body"
            >{j}</div>
        )
      )
    }
    </div>
  </div>)
}
export default Board;
