import React, { useState, useEffect, useRef } from 'react';
import { run } from "../functions/run";
import { makeBoard } from "../functions/makeBoard";
import Popup from './Popup';
import { patterns } from "../functions/patterns";


const useInterval = (callback, delay) => {
  const savedCallback = useRef();

  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  useEffect(() => {
    function tick() {
      savedCallback.current();
    }
    if (delay !== null) {
      let id = setInterval(tick, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
}

const rules =
    `Rules`

     `Any live cell with fewer than two live neighbours dies, as if by underpopulation.
     Any live cell with two or three live neighbours lives on to the next generation.
     Any live cell with more than three live neighbours dies, as if by overpopulation.
     Any dead cell with exactly three live neighbours becomes a live cell, as if by reproduction.`


export const Menu = ({board}) => {

  let [currentBoard, setBoard] = board
  let [counter, setCounter] = useState(0);
  let [delay, setDelay] = useState(null);
  let [running, setRun] = useState(false);
  const speeds = [ 1000, 500, 100 ];

  useInterval(() => {
    run(currentBoard,setBoard);
    setCounter(counter + 1);
  }, delay);

  const handleClickMenu = (e) => {
    switch(e.target.id){
      case "Start":
        setDelay(running ? null : speeds[1]);
        setRun(!running);
        break;
      case "Randomize":
        setCounter(0);
        setBoard(makeBoard(-1));
        break;
      case "Clear Board":
        setBoard(makeBoard(0));
        setCounter(0);
        setDelay(null);
        setRun(false);
        break;
      default:
        alert("no option was selected");
    }
  }

  return (
    <div className="wrapper">
      <div className="counter-label flex">
        <p>Generations</p> 
        <span className="counter">{counter}</span>
      </div>
      <div>
        <i className="fas fa-twitter"></i>
      </div>
      {["Start", "Randomize", "Clear Board"]
      .map(i => <div id={i} className="menuButton" onClick={handleClickMenu}>{i}</div>)}
      <Popup id="Rules" className="menuButton" body={rules}/>
      <Popup id="Patterns" className="menuButton" body={patterns}/>
    </div>
  )
}
export default  Menu;
