import React, { useState } from 'react';
let board = [];
for(let i = 0; i< 30; i++){
    board.push([]);
  for(let j = 0; j < 50; j++){
    board[i].push(Math.random() < .5 ? 1 : 0);
  }
}

export const Board = () => {
  const [boardf, setBoard] = useState(board);
  const handleClick = (e) => {
      const x = e.target.id.split(",")[0];
      const y = e.target.id.split(",")[1];
        boardf[x][y] = boardf[x][y] === 0 ? 1 : 0;
  }
  return (<div>
    {
    boardf
      .map(
        (i, index1) => i.map(
            (j, index2) => <div
                id={[index1, index2]}
                onClick={handleClick}
                key={index1+""+index2}
                className="square"
            >{j}</div>
        )
      )
    }
  </div>)
}
export default Board;
