export const run = (currentBoard, setBoard) => {

  const findNeightbours = (body, y, x, list)=>{
    let countNeightbours = 0;

    const checkCount = () => {
      if(countNeightbours === 4) return true;
      return false;
    }

    const checkNeightbour = (y, x, list) => {
      try{
        if(!list[y][x]) return false;
      }catch(e){
        return false;
      }
      return true;
    }

    if(body === 1){
      if(checkNeightbour(y-1,x-1,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y-1,x,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y-1,x+1,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y,x-1,list)){
        countNeightbours++;
        if(checkCount()) return false;
      }
      if(checkNeightbour(y,x+1,list)){
        countNeightbours++;
        if(checkCount()) return false;
      }
      if(checkNeightbour(y+1,x-1,list)){
        countNeightbours++;
        if(checkCount()) return false;
      }
      if(checkNeightbour(y+1,x,list)){
        countNeightbours++;
        if(checkCount()) return false;
      }
      if(checkNeightbour(y+1,x+1,list)){
        countNeightbours++;
        if(checkCount()) return false;
      }
      return countNeightbours < 2 ? false : true;
    }else{
       if(checkNeightbour((y-1),(x-1),list)){
        countNeightbours++;
      }

      if(checkNeightbour(y-1,x,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y-1,x+1,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y,x-1,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y,x+1,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y+1,x-1,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y+1,x,list)){
        countNeightbours++;
      }
      if(checkNeightbour(y+1,x+1,list)){
        countNeightbours++;
      }
      if(countNeightbours === 3)return true;
      return false;
    }
  }
}
